DROP TABLE IF EXISTS pocketboss;
CREATE TABLE pocketboss (
	`id` INT NOT NULL PRIMARY KEY,
	`pb` VARCHAR(30),
	`pb_location` VARCHAR(50),
	`bp_mob` VARCHAR(100),
	`bp_lvl` SMALLINT,
	`bp_location` VARCHAR(50),
	`type` VARCHAR(25),
	`slot` VARCHAR(25),
	`line` VARCHAR(25),
	`ql` SMALLINT,
	`itemid` INT
);
